.large-btn{
    font-size: 2rem;
    position: relative;
    display: inline-grid;
    z-index: 1;
    padding-inline: 2em;
    border-radius: 50%;
    aspect-ratio: 1;
    place-items: center;
    text-decoration: none;    
}

.large-btn::after{
    content: '';
    position: absolute;
    background-color: hsl( 0 0% 100% /.15);
    width: 100%;
    height: 100%;
    z-index: -1;
    border-radius: inherit;
    opacity: 0;
    transition:opacity 500ms linear, transform 500ms ease-in-out;
}

.large-btn:hover::after,
.large-btn:focus::after{
    opacity: 1;
    transform: scale(1.5);
}

// primary-header

.primary-header{
    justify-content: space-between;
    align-items: center;
}

.logo{
    margin: 1.5rem clamp(1.5rem, 5vw, 3.5rem);
}

.primary-navigation{    
    gap: clamp(1.5rem, 5vw, 3.5rem);    
    z-index: 1000;
    list-style: none;
    margin: 0;
    padding: 0;
    background: rgba(0, 0, 0, 0.95);        
}

.mobile-nav-toggle{
    display: none;
}



@supports(backdrop-filter: blur(1.5rem)){
    .primary-navigation{    
        background: rgba( 255, 255, 255, 0.05 );     
        backdrop-filter: blur(1.5rem);   
        -webkit-backdrop-filter: blur( 4.5px );           
    }  
}


@media(max-width:35rem){
    .primary-navigation{
        gap: 2rem;        
        position: fixed;        
        inset: 0 0 0 30%;    
        z-index: 1000;
        list-style: none;
        margin: 0;
        padding: min(20rem, 10vh) 2rem;
        flex-direction: column;
        transform: translateX(100%); 
        transition: transform 0.5s ease-in-out;
    }

    .primary-navigation[data-visible="true"]{
        transform: translateX(0);
    }

    .primary-navigation.underline-indicator > .active{
        border: 0;
    }

    .primary-navigation > *{
        padding: .5rem 0;
    }

    .mobile-nav-toggle{
        display: block;
        position: absolute;
        z-index: 2000;
        right: 1rem;
        top: 2rem;
        background: none;
        background-image: url("/assets/shared/icon-hamburger.svg");
        background-repeat: no-repeat;
        background-position: center;
        width: 1.5rem;
        aspect-ratio: 1;
        border: 0;    
        transition: all 0.5s ease-in-out;    
    }   
    
    .mobile-nav-toggle[aria-expanded="true"]{
        background-image: url("/assets/shared/icon-close.svg");
    }

    .mobile-nav-toggle:focus-visible{
        outline: 5px solid white;
        outline-offset: 5px;
    }

    .close{        
        transform: translateX(0);
    }

    .close-btn{
        background-image: url("/assets/shared/icon-close.svg");
    }
}

@media (min-width:35em){
    .primary-navigation{
        padding-inline: clamp(2rem, 7vw, 7rem);
    }

}

@media (min-width:35em) and (max-width:47.999em){
    .primary-navigation a > span{
        display: none;
    }

    .underline-indicator > li{
        padding: 2rem 0;            
    }
}

@media(max-width: 48em){
    .flex-destination{
        align-items: center;
