    padding-inline: 1.5em;
    border-radius: 50%;
    aspect-ratio: 1;
    background-color: hsl( $clr-dark) ;
    color: hsl( $clr-white) ;
    border: 1px solid hsl(0 0% 100%/ .25);
}

.number-list > *:hover,
.number-list > *:focus{
    background-color: $clr-dark;
    border-color: hsl( $clr-white) ;
    color: $clr-white;
}

.number-list > .active{
    background-color: hsl( 0 0% 100% ) ;
    color: hsl( $clr-dark);
}

