/* -------------------------- */
/* Layout                     */
/* -------------------------- */

.grid-container{
    text-align: center;
    display: grid;
    // border: 5px solid cyan;    
    place-items: center;
    padding-inline: 2em;
    // place-content: center;
    // row-gap: 10%;
    padding-bottom: 4rem;
}

.grid-container p:not([class]){
    max-width: 50ch;
    // outline: 3px solid limegreen;
}

.numbered-title{
    grid-area: title;
}

/* -------------------------- */
/* Destination Layout         */
/* -------------------------- */

.grid-container--destination{
    --flow-space: 2rem;
    grid-template-areas: 
        'title'
        'image'
        'tabs'
        'content';
        
        img{
            grid-area: image;
            max-width: 60%;   
            place-self: center;         
        }

        .tab-list{
            grid-area: tabs;    
        }

        .destination-info{
            grid-area: content;    
        }


}

.destination-meta{
    flex-direction: column;        
    border-top: 1px solid hsl( 0 0% 100% /.25);
    padding-top: 2.5rem;
    margin-top: 2.5rem;                  
}

.destination-meta p{
      font-size: 1.75rem;
}

@media (min-width:35em){
    .numbered-title{
        justify-self: start;
        margin-top: 2rem;
    }
    .destination-meta{
        flex-direction: row;  
        justify-content: space-evenly;                     
    }
}

@media (min-width: 48em){
    .grid-container{
        text-align: left;
        column-gap: 2rem;
        grid-template-columns: minmax(1em, 1fr) repeat(2, minmax(0, 40em)) minmax(1em, 1fr);
    }

    .grid-container--home{
        padding-bottom: max(6rem,20vh);
        align-items: end;
    }

    .grid-container--home > *:first-child{
        grid-column: 2;
        // outline: 1px solid red;
    }
    
    .grid-container--home > *:last-child{
        grid-column: 3;
        // outline: 1px solid yellow;    
    }

    .grid-container--destination{     
        justify-items: start;   
        align-content: start;
        grid-template-areas: 
            '. title title .'
            '. image tabs .'            
            '. image content .';        
    }

    .destination-meta{
        flex-direction: row;  
        justify-content: start;    
        gap: min(3vw,6rem);
    }
}

/* -------------------------- */
/* Crew Layout                */
/* -------------------------- */

.grid-container--crew{
    --flow-space: 2rem;
    grid-template-areas: 
        'title'
        'image'
        'dots'
        'content';

        img{
            grid-area: image;
            max-width: 70%;               
            place-self: center;   
            border-bottom: 1px solid hsl( 0 0% 100% /.25);               
        }

        .dots-indicator{
            grid-area: dots;    
        }

        .crew-info{
            grid-area: content;    

            h2{
                color: hsl( 0 0% 100% /.5);        

            }
        
            header{
                margin-block-end: 3rem;
                
                p{
                    color: hsl( $clr-white );
                }
            }

            p{
                color: hsl( $clr-light );
            }
        }        
}

@media (min-width:35em) and (max-width:47.999em){
    .grid-container.grid-container--crew{
        padding-bottom: 0rem;
    }

    .grid-container--crew{
        grid-template-areas: 
        'title'
        'content'
        'dots'
        'image';

        img{
            max-width: 60%;
        }
    }
}

@media (min-width: 48em){
    .grid-container.grid-container--crew{
        padding-bottom: 0rem;
    }
    

    .grid-container--crew{        
        grid-template-areas: 
        '. title image .'
        '. content image .'
        '. dots image .';  

        .crew-info{
            align-self: center;
        }

        img{
            align-self: end;
            max-width: 80%;            
        }
    }

    .grid-container.grid-container--crew{
        place-items: start;
    }
}

/* -------------------------- */
/* Technology Layout          */
/* -------------------------- */
.grid-container--technology{
    grid-template-areas: 
    'title'
    'image'
    'number'
    'content';

    h2{
        font-size: 1rem;
    }

    header p {
        color: hsl( $clr-white );
    }

    p{
        color: hsl( $clr-light );
    }

    .technology-pic-1{
        grid-area: image;    
    }

    .technology-pic-2{
        display: none;
    }

    .number-list{
        grid-area: number;
    }

    .technology-info{
        grid-area: content;
    }
}

.grid-container.grid-container--technology{
    padding-inline: 0;
}

@media (min-width:35em) and (max-width:47.999em){
    .grid-container--technology{
        .numbered-title{
            padding-inline: 2em;
        }
    }
}

@media (min-width: 48em){
    .grid-container.grid-container--technology{        
        grid-template-columns: clamp(1em,2vw,3em) minmax(0, 10em) minmax(1em,1fr) minmax(1em,1fr);
    }
    .grid-container--technology{        
        grid-template-areas: 
        '. title title title .'
        '. number content image image';        

        .technology-pic-1{
            display: none;    
        }
        
        .technology-pic-2{
            grid-area: image;
            display: block;
            justify-self: end;
            min-height: 65%;
        }

        .number-list.flex{
            flex-direction: column;
        }
    }
}
